import React, {Component} from 'react'
import { API_ROOT } from '../constants';
import dahlia from "../images/DahliaHeader.jpg"
import Logo from "../images/plant.svg"

class HomeContainer extends Component {

	state = {
		user: {}
	}

	componentDidMount() {
		this.getProfile()
	}

    getProfile = () => {
		let token = localStorage.getItem('jwt')
		if (token) {
			fetch(`${API_ROOT}/profile`, {
				headers: {
					'Authorization': 'Bearer ' + token
				}
			})
			.then(resp => resp.json())
			.then(json => {
				console.log('profile:', json)
				const {username, plant_care_rating, avatar} = json.user
				this.setState({ user: { username, plant_care_rating, avatar} })
			})
		}
	}

	renderProfile = () => {
		return <p>Welcome Home {this.state ? this.state.user.name : null}</p>
	}

	render() {
		return (
			<div className="HomeContainer">
				<div id="header" >
					<img src={dahlia} className="ui fluid image" />
				</div>

				<div className="ui borderless main menu">
				    <div className="ui text container">
				      <div className="item"><img className="svg" src={Logo}/></div>
				      <div href="#" className="item"><strong>Plant Pals</strong></div>
				      <a href="#" className="item">My Plants</a>
				      <a href="#" className="item">Articles</a>
				      <div href="#" className="ui right floated item"><strong>Hello {this.state ? this.state.user.name : null}</strong></div>
				    </div>
				  </div>
				{this.renderProfile()}
				<div class="content">
				  <div class="ui large star rating" data-rating="2" data-max-rating="5"></div>
				</div>
				<div className="ui inverted vertical footer segment">
				    <div className="ui center aligned container">
					  <img src={Logo} className="ui centered mini image"></img>
				      <div className="ui horizontal inverted small divided link list">
				        <a className="item" href="#">Contact Us</a>
				        <a className="item" href="#">Terms and Conditions</a>
				        <a className="item" href="#">Privacy Policy</a>
				      </div>
				    </div>
				  </div>
			</div>
		);
	}
}

export default HomeContainer
