import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { Dropdown } from 'semantic-ui-react';
import Dropzone from 'react-dropzone';

class SignUp extends Component {

    constructor() {
        super()
        this.state = {
            username: '',
            password: '',
            rating: null,
            avatar: ''
        }
    }

    handleSubmit = event => {
        const person = this.state
        this.props.onSignUp(event, person)
        this.props.history.push('/home')

    }

    handleFileDrop = files => {
        this.setState({
            avatar: files[0],
        });
    };

    handleChange = event => {
        event.preventDefault()
        console.log(event)
        this.setState({
            [event.target.name]: [event.target.value]
        });
    };

    render() {
        const options = [
          {
            key: '1 - Sprout',
            text: '1 - Sprout',
            value: 1
          },
          {
            key: '2 - Seedling',
            text: '2 - Seedling',
            value: 2
          },
          {
            key: '3 - Budding',
            text: '3 - Budding',
            value: 3
          },
          {
            key: '4 - Flowering',
            text: '4 - Flowering',
            value: 4
          },
          {
            key: '5 - One with the trees',
            text: '5 - One with the trees',
            value: 5
          }
        ]
        return (
            <div className="signup">
                <br></br>
                <div className="page-login">
                    <div className="ui centered grid container">
                        <div className="nine wide column">
                            <div className="ui warning message">
                                <div className="content">
                                    <div className="header">
                                        Signup!
                                        </div>
                                    <p>Please Sign Up To Find a new Plant Pal!</p>
                                </div>
                            </div>

                                <div className="content">
                                    <form className="ui form" onSubmit={this.handleSubmit}>
                                        <div className="field">
                                            <label>Enter Username</label>
                                            <input type="text" name="username" placeholder="Username" value={this.state.username} onChange={this.handleChange}></input>
                                        </div>
                                        <div className="field">
                                            <label>Enter Password</label>
                                            <input type="password" name="password" placeholder="Password" value={this.state.password} onChange={this.handleChange}></input>
                                        </div>
                                        <label>Select Plant Level</label>
                                        <Dropdown
                                            placeholder='Select Comfort Level of Plant Care'
                                            fluid
                                            selection
                                            onChange={this.handleChange}
                                            options={options}
                                        /><br />
                                        <ReactDropzone onDrop={this.handleFileDrop}>
                                            {({getRootProps}) => (
                                                <div {...getRootProps()}>
                                                    <p>Drop files here, or click to select files</p>
                                                </div>
                                            )}
                                        </ReactDropzone>
                                        <Dropzone onDrop={acceptedFiles => console.log(acceptedFiles)}>
  {({getRootProps, getInputProps}) => (
    <section>
      <div {...getRootProps()}>
        <input {...getInputProps()} />
        <p>Drag 'n' drop some files here, or click to select files</p>
      </div>
    </section>
  )}
</Dropzone>
                                        {this.state.avatar ?
                                            <div>
                                                <h2>Uploading {this.state.avatar.length} files...</h2>
                                                <img width="100px" src={this.state.avatar.preview} alt="profile"/>
                                            </div>
                                        : null}
                                        <button className="ui primary button" type="submit">
                                            Sign Up!
                                        </button>
                                    </form>
                                </div>

                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default withRouter(SignUp);
