import React, {Component} from 'react'
import { API_ROOT } from '../constants';

class HomeContainer extends Component {

	state = {
		user: {}
	}

	componentDidMount() {
		this.getProfile()
	}

    getProfile = () => {
		let token = localStorage.getItem('jwt')
		if (token) {
			fetch(`${API_ROOT}/profile`, {
				headers: {
					'Authorization': 'Bearer ' + token
				}
			})
			.then(resp => resp.json())
			.then(json => {
				console.log('profile:', json)
				const {username, plant_care_rating, avatar} = json.user
				this.setState({ user: { username, plant_care_rating, avatar} })
			})
		}
	}

	renderProfile = () => {
		return <p>Welcome Home {this.state ? this.state.user.name : null}</p>
	}

	render() {
		return (
			<div className="HomeContainer">
				<div class="ui main text container">
					<h1 class="ui header">Sticky Example</h1>
					<p>This example shows how to use lazy loaded images, a sticky menu, and a simple text container</p>
				</div>
				<div class="ui borderless main menu">
				    <div class="ui text container">
				      <div class="header item">
				        <img class="logo" src="assets/images/logo.png"></img>
				        Plant Pals
				      </div>
				      <a href="#" class="item">My Plants</a>
				      <a href="#" class="item">Articles</a>
				      <a href="#" class="ui right floated item">Hello {this.state ? this.state.user.name : null} </a>
				    </div>
				  </div>
				{this.renderProfile()}
			</div>
		);
	}
}

export default HomeContainer
