import React, {Component} from 'react'
import { API_ROOT } from '../constants';

class HomeContainer extends Component {

	state = {
		user: {}
	}

	componentDidMount() {
		this.getProfile()
	}

    getProfile = () => {
		let token = localStorage.getItem('jwt')
		if (token) {
			fetch(`${API_ROOT}/profile`, {
				headers: {
					'Authorization': 'Bearer ' + token
				}
			})
			.then(resp => resp.json())
			.then(json => {
				console.log('profile:', json)
				const {username, plant_care_rating, avatar} = json.user
				this.setState({ user: { username, plant_care_rating, avatar} })
			})
		}
	}

	renderProfile = () => {

		return <p>Welcome Home {this.state ? this.state.user.name : null}</p>
	}

	render() {
		return (
			<div className="HomeContainer">
				{this.renderProfile()}
			</div>
		);
	}
}

export default HomeContainer
