import React, { Component } from 'react';
import './App.css';

class App extends Component {
	state = {
		plants: [],
		validPlantsIds: []
	}

	componentDidMount() {
		this.findPlants('blackwood')
	}

	render() {

		return (
			<div className="App">

			</div>
		);
	}

	findPlants = name => {
		const proxyUrl = 'https://cors-anywhere.herokuapp.com/' 
		const URL = `https://trefle.io/api/plants?q=${name}`
		fetch(proxyUrl + URL, {
			method: 'GET',
			headers: {
				'Content-Type': 'application/json',
				'Authorization': 'Bearer OVUxRkpUM2dkeXpRaFZjd2JqVFlGZz09',
      			Accept: "application/json"
			}
		})
		.then(res => res.json())
		.then(json => {
			this.setState({plants: json})
			this.extractValidPlantURL(json)
		})
	}

	extractValidPlantURL = json => {
		let validIds = []
		for (var i = 0; i < json.length; i++) {
			console.log(json[i])
			if (json[i].complete_data) {
				validIds.push(json[i].url)
			}
		}
		this.setState({ validPlantIds: validIds})
	}




}

export default App;
