import React, {Component} from 'react'
import { API_BASE } from '../constants';
import { Card, Image } from 'semantic-ui-react'


class Room extends Component {

	handleClick = () => {
		this.props.handleClick(this.props.room)
	}

	render() {
		const { nickname, notes, watering_delay_days } = this.props.plant
		return (
		<Card color="green" onClick={this.handleClick}>
			<Image src="https://www.bhg.com.au/media/18730/06072018-indoor-plants.jpg?width=400&mode=crop&center=0.0,0.0" wrapped ui={false} rounded />
			<Card.Content>
				<Card.Header>{nickname}</Card.Header>
				<Card.Description>
					{notes}
				</Card.Description>
			</Card.Content>
			<Card.Content extra>
				Water every {watering_delay_days} days
			</Card.Content>
		</Card>
		)
	}
}

export default Room
