import React, { Component } from 'react';
import './App.css';
import { API_ROOT } from './constants';

import { BrowserRouter as Router, Route } from 'react-router-dom';
import HomeContainer from './containers/HomeContainer'
import Login from './components/Login'
import SignUp from './components/SignUp'


class App extends Component {

	state = {
		user: {}
	}

	logout = event => {
		this.clearToken()
		this.setState({ user: '' })
	}

	clearToken(jwt) {
		localStorage.setItem('jwt', '')
	}

	getProfile = () => {
		let token = localStorage.getItem('jwt')
		fetch(`${API_ROOT}/profile`, {
			headers: {
				'Authorization': 'Bearer ' + token
			}
		})
		.then(resp => resp.json())
		.then(json => {
			console.log('profile:', json)
			const {username, plant_care_rating, avatar} = json.user
			this.setState({ user: { username, plant_care_rating, avatar} })
		})
	}

	getSignUp = (event, person) => {
		event.preventDefault()
		console.log(person)
		debugger

		let username = person.username
		let password = person.password


	fetch(`${API_ROOT}/new`, {
		method: 'POST',
		headers: {
			'Content-Type': 'application/json'
		},
		body: JSON.stringify({ user: { username, password } })
		})
		.then(resp => resp.json())
		.then(json => {
			console.log('login:', json)
			if (json && json.jwt) {
				this.saveToken(json.jwt)
				this.setState({ user: {username}})
			}
		})
		.then(() => {
			this.getProfile()
		})
	}

	saveToken(jwt) {
		localStorage.setItem('jwt', jwt)
	}

	render() {
		return (
			<Router>
				<div className="App">
					{/*<NavBar documents={documents} onLogout={this.logout} />*/}
					<Route exact path="/" render={ (props) => 
						<Login {...props} onLogin={this.getProfile} onLogout={this.logout}/>} 
					/>
					<Route path='/home' render={(props) => 
						<HomeContainer {...props} {...this.state} />} 
					/>
					<Route path='/signup' render={(props) => <SignUp
						{...props} onSignUp={this.getSignUp} />}
					/>
				</div>
			</Router>
		);
	}
}

export default App;
