import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
	state = {
		plants: [],
		validPlantsIDs: []
	}

	componentDidMount() {
		this.findPlants('blackwood')
	}

	render() {
		return (
			<div className="App">
				Learn React
			</div>
		);
	}

	findPlants = name => {
		const proxyUrl = 'https://cors-anywhere.herokuapp.com/' 
		const URL = `https://trefle.io/api/plants?q=${name}`
		fetch(proxyUrl + URL, {
			method: 'GET',
			headers: {
				'Content-Type': 'application/json',
				'Authorization': 'Bearer OVUxRkpUM2dkeXpRaFZjd2JqVFlGZz09'
			}
		})
		.then(res => res.json())
		.then(json => this.extractValidPlantURL(json))
	}

	extractValidPlantURL = json => {
		for (let plant of json) {
			if (plant.complete_data) {
				this.getPlantData(plant.id)
				break
			}
		}
	}


}

export default App;
